INSERT INTO `dl_options` VALUES("236", "_transient_feed_4cf2927c306176c13771b3ef386d2f5e", "a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:28:\"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n	\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:3:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:10:\"WPBeginner\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:26:\"https://www.wpbeginner.com\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:30:\"Beginner\'s Guide for WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:13:\"lastBuildDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 18 Dec 2018 12:15:47 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"en-US\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"generator\";a:1:{i:0;a:5:{s:4:\"data\";s:30:\"https://wordpress.org/?v=4.9.9\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"item\";a:3:{i:0;a:6:{s:4:\"data\";s:51:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:45:\"How to Properly Move from Medium to WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"https://www.wpbeginner.com/wp-tutorials/how-to-properly-move-from-medium-to-wordpress/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:95:\"https://www.wpbeginner.com/wp-tutorials/how-to-properly-move-from-medium-to-wordpress/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 17 Dec 2018 12:53:22 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:4:{i:0;a:5:{s:4:\"data\";s:9:\"Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:40:\"how to transfer from medium to wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:19:\"medium to wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:28:\"medium to wordpress tutorial\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"http://www.wpbeginner.com/?p=36193\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:670:\"<p>Do you want to move your Medium blog to WordPress? While Medium is a decent blogging platform, it is certainly no match for the flexibility of WordPress. In this article, we will show you how to properly move your blog from Medium to WordPress without&#8230;&#160;<strong><a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-properly-move-from-medium-to-wordpress/\">Read More &#187;</a></strong></p>\n<p>The post <a rel=\"nofollow\" href=\"https://www.wpbeginner.com/wp-tutorials/how-to-properly-move-from-medium-to-wordpress/\">How to Properly Move from Medium to WordPress</a> appeared first on <a rel=\"nofollow\" href=\"https://www.wpbeginner.com\">WPBeginner</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Editorial Staff\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:23181:\"<p>Do you want to move your Medium blog to WordPress? While Medium is a decent blogging platform, it is certainly no match for the flexibility of WordPress. In this article, we will show you how to properly move your blog from Medium to WordPress without losing your content. </p>\n<p><img title=\"Moving from Medium to WordPress\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/mediumtowp.png\" alt=\"Moving from Medium to WordPress\" width=\"550\" height=\"340\" class=\"alignnone size-full wp-image-58335\" /></p>\n<h4>Why You Should Move from Medium to WordPress?</h4>\n<p>WordPress is the most <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-choose-the-best-blogging-platform/\" title=\"How to Choose the Best Blogging Platform in 2018 (Compared)\">popular blogging platform</a> in the world. It powers more than 32% of all websites on the internet. </p>\n<p>WordPress is very easy to use, incredibly powerful, and easy to customize. To learn more about WordPress and what you can do with it, see our complete <a href=\"https://www.wpbeginner.com/opinion/the-ultimate-wordpress-review-is-it-the-best-choice-for-your-website/\" title=\"The Ultimate WordPress Review – Is It the Best Choice For Your Website?\">WordPress review</a> with pros and cons. </p>\n<p>Now, when we say WordPress, we are talking about self-hosted WordPress.org. Not to be confused with WordPress.com, which is a hosted platform. For a full comparison see our article on the difference between <a href=\"https://www.wpbeginner.com/beginners-guide/self-hosted-wordpress-org-vs-free-wordpress-com-infograph/\" title=\"WordPress.com vs WordPress.org – Which is Better? (Comparison Chart)\">WordPress.com vs WordPress.org</a>. </p>\n<p>While Medium is a good blogging platform for new writers, many users quickly realize that it&#8217;s quite limited <a href=\"https://www.wpbeginner.com/opinion/wordpress-vs-medium-which-one-is-better-pros-and-cons/\" title=\"WordPress vs. Medium – Which One is Better? (Pros and Cons)\">when compared to WordPress</a>. You don&#8217;t have full control of your website and customization options. You also can&#8217;t monetize it the way you may want to. </p>\n<p>If you have decided to switch from Medium to WordPress, then we want to congratulate you on making the right decision. We have created this complete step by step guide to help you move all your content from Medium to WordPress.</p>\n<p><strong>Bonus Free Offer:</strong> Since a lot of users have asked for this, we are now offering a free Medium to WordPress migration service as part of our <a href=\"https://www.wpbeginner.com/free-wordpress-blog-setup/\" title=\"Free WordPress blog setup service\">free WordPress blog setup</a>. This means one of our expert team members will do the entire migration for you (100% free). Yes, you can literally switch from Medium to WordPress without any risk.</p>\n<p>However, if you are someone who likes learning and doing things yourself, then you can follow our step by step tutorial below.</p>\n<p>Here is a breakdown of all the steps that we will be covering in this step by step tutorial:</p>\n<ul>\n<li>Install and Setup WordPress</li>\n<li>Export your Medium stories</li>\n<li>Import Medium stories into WordPress</li>\n<li>Import Images from Medium to WordPress</li>\n<li>Setup redirects (If you have a custom domain on Medium)</li>\n<li>Installing a WordPress theme</li>\n<li>Installing essential WordPress plugins</li>\n<li>Improving your WordPress skills</li>\n</ul>\n<h4>Video Tutorial</h4>\n<p><span class=\'embed-youtube\' style=\'text-align:center; display: block;\'><iframe class=\'youtube-player\' type=\'text/html\' width=\'520\' height=\'323\' src=\'https://www.youtube.com/embed/0Wj-WUlL6a4?version=3&#038;rel=0&#038;fs=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;wmode=transparent\' frameborder=\'0\' allowfullscreen></iframe></span></p>\n<p>If you don&#8217;t want to watch the video tutorial, then you can continue reading the text version below:</p>\n<h4>Step 1. Install and Setup WordPress</h4>\n<p>To get started with WordPress, you&#8217;ll need the following two things. </p>\n<ul>\n<li>A web hosting account. This is where all your website files are stored.</li>\n<li>A domain name. This will be your website&#8217;s address on the internet and what users will type in their browser to reach your website (For example, wpbeginner.com)</li>\n</ul>\n<p>A domain name typically costs around $14.99 / year and website hosting starts from $7.99 per month. </p>\n<p>Thankfully, <a title=\"Bluehost\" href=\"https://www.wpbeginner.com/refer/bluehost/\" rel=\"nofollow\" target=\"_blank\">Bluehost</a>, an official WordPress recommended hosting provider has agreed to offer our users a free domain name and over 60% off their web hosting service. </p>\n<p><a title=\"Bluehost\" href=\"https://www.wpbeginner.com/refer/bluehost/\" rel=\"nofollow\" target=\"_blank\" class=\"orangecta\">&rarr;Click Here to Claim This Exclusive Bluehost Offer &larr;</a></p>\n<p>If for some reason you want other options, then you can look at our list of the <a href=\"https://www.wpbeginner.com/wordpress-hosting/\" title=\"How to Choose the Best WordPress Hosting?\">best WordPress hosting companies</a>.</p>\n<p>Once you have set up your domain and hosting, the next step is to install WordPress. </p>\n<p>Most WordPress hosting companies offer 1-click install options that you can use to instantly install WordPress. If you can&#8217;t find it, then ask your hosting provider&#8217;s support, and they will set it up for you. </p>\n<p>You can also do it on your own. See our complete guide on <a href=\"https://www.wpbeginner.com/how-to-install-wordpress/\" title=\"How to Install WordPress – Complete WordPress Installation Tutorial\">how to install WordPress</a> for step by step instructions. </p>\n<p>After you’re done installing WordPress, you’re ready to move on to the next step which is to export your content from Medium.</p>\n<h4>Step 2. Exporting Your Data from Medium</h4>\n<p>Previously, it was quite difficult to export your data from Medium in a WordPress compatible format. </p>\n<p>We decided to solve this problem and developed a <a href=\"https://mediumtowp.com/\" title=\"Medium to WordPress Importer\">Medium to WordPress Importer</a> tool.</p>\n<p>It is a free online tool that imports your content from Medium and converts it into a WordPress compatible export file. It does not make any changes to your existing Medium content, so it is perfectly safe to use.</p>\n<p>Head over to <a href=\"https://mediumtowp.com/\" title=\"Medium to WordPress Importer\">Medium to WordPress Importer</a> website and enter your Medium blog URL. </p>\n<p><img title=\"Enter your Medium blog URL\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2018/12/mediumtowpimporter.png\" alt=\"Enter your Medium blog URL\" width=\"550\" height=\"291\" class=\"alignnone size-full wp-image-58316\" /></p>\n<p>Now, if your Medium blog URL is using a custom domain name and has a URL like http://example.com or https://something.example.com, then enter the URL with your name and email address. </p>\n<p>On the other hand, if you don&#8217;t have a custom domain name, then the Medium to WordPress Importer will ask you to upload the Medium export file. </p>\n<p><img title=\"Upload Medium export file\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/uploadexportfile.png\" alt=\"Upload Medium export file\" width=\"550\" height=\"297\" class=\"alignnone size-full wp-image-58317\" /></p>\n<p>Here is how you can get that export file from Medium. </p>\n<p>First, you need to login to your Medium account and click on your profile photo at the top right corner of the screen. This will bring down a menu. From here you need to click on the &#8216;Settings&#8217; link. </p>\n<p><img title=\"Go to Medium settings page\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2018/12/mediumsettings.png\" alt=\"Go to Medium settings page\" width=\"550\" height=\"378\" class=\"alignnone size-full wp-image-58318\" /></p>\n<p>This will take you to the settings page where you need to scroll down to the &#8216;Download Your Information&#8217; section. Click on the &#8216;Download zip&#8217; button to export your Medium data. </p>\n<p><img title=\"Download zip file\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2018/12/downloadzip.png\" alt=\"Download zip file\" width=\"550\" height=\"148\" class=\"alignnone size-full wp-image-58319\" /></p>\n<p>On the next page, you need to click on the export button. Medium will then prepare your download and send a link to you via email. </p>\n<p>You need to check your email to see if you get the email from Medium. You need to follow the link in the email to download your Medium export file. </p>\n<p>Once you have downloaded the Medium export file, you can switch to the Medium to WordPress Importer tool and upload that file. </p>\n<p><img title=\"Upload your Medium export file\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/uploadingmediumdata.png\" alt=\"Upload your Medium export file\" width=\"550\" height=\"278\" class=\"alignnone size-full wp-image-58321\" /></p>\n<p>Next, click on the &#8216;Export My Medium Website&#8217; button to continue. </p>\n<p>Medium to WordPress Importer will now prepare your export file. Once finished, it will show you a success message with a button to download your WordPress ready Medium export file. </p>\n<p><img title=\"Download WordPress compatible Medium export file\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2018/12/downloadexportfile.png\" alt=\"Download WordPress compatible Medium export file\" width=\"550\" height=\"293\" class=\"alignnone size-full wp-image-58322\" /></p>\n<p>Go ahead and download the export file to your computer. You will need that file in the next step. </p>\n<h4>Step 3. Importing Your Medium Articles into WordPress</h4>\n<p>Now that you have successfully exported your Medium content into a WordPress compatible format, it is time to import that content into your new WordPress blog. </p>\n<p>Visit the WordPress <a href=\"https://www.wpbeginner.com/glossary/admin-area/\" title=\"Admin Area\">admin area</a> of your website and go to <strong>Tools &raquo; Import</strong> page. </p>\n<p>You will see a list of importers available for different platforms. You need to scroll down to WordPress and then click on the &#8216;Install Now&#8217; link.</p>\n<p><img title=\"Install WordPress importer\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/installwpimporter.png\" alt=\"Install WordPress importer\" width=\"550\" height=\"298\" class=\"alignnone size-full wp-image-58323\" /></p>\n<p>WordPress will now fetch the importer plugin and install it on your WordPress site without reloading the page. Once it is installed, you need to click on &#8216;Run Importer&#8217; to launch it.</p>\n<p><img title=\"Run WordPress importer\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/runimporter-1.png\" alt=\"Run WordPress importer\" width=\"550\" height=\"319\" class=\"alignnone size-full wp-image-58324\" /></p>\n<p>This will bring you to the WordPress importer page. Go ahead and click on &#8216;Choose file&#8217; button to select the export.xml file you downloaded earlier.</p>\n<p>Next, click on the &#8216;Upload file and import&#8217; button to continue.</p>\n<p>WordPress importer will now upload your Medium export file and analyze it. On the next screen, it will ask you to assign authors.</p>\n<p><img title=\"Import options\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2018/12/importoptions.png\" alt=\"Import options\" width=\"550\" height=\"325\" class=\"alignnone size-full wp-image-58325\" /></p>\n<p>You can import the author from your Medium website, create a new author, or assign all content to your existing WordPress user.</p>\n<p>Don’t forget to check the box next to &#8216;Download and import file attachments&#8217; option. It will fetch images from your Medium website into your WordPress media library. </p>\n<p>You can now click on the Submit button to run the importer. </p>\n<p>WordPress will now import content from the Medium export file. It will also try to fetch images in your Medium articles.</p>\n<p>Upon completion, you will see a success message.</p>\n<p><img title=\"Success message\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/success.png\" alt=\"Success message\" width=\"550\" height=\"293\" class=\"alignnone size-full wp-image-58326\" /></p>\n<p>Congratulations, you have successfully imported content from Medium to WordPress. You can now go to the posts page in your WordPress admin area to see if all your content is there. </p>\n<p>If the import process fails in the middle, then you can always run it again. The importer is smart enough to detect duplicate content and skip it. It will only import the content that didn’t import successfully in the previous attempt.</p>\n<h4>Step 4. Importing Your Images From Medium to WordPress</h4>\n<p>The WordPress importer tries to import images from your Medium articles to the WordPress media library. However, it may fail due to the way Medium displays images in your articles. </p>\n<p>You need to go to the <strong>Media &raquo; Library</strong> page to see all images that have been imported successfully. </p>\n<p><img title=\"Media library\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/medialibrary.png\" alt=\"Media library\" width=\"550\" height=\"276\" class=\"alignnone size-full wp-image-58331\" /></p>\n<p>If some or all of your images failed to import, then you will need to import them again. </p>\n<p>First, you need to install and activate the <a href=\"https://wordpress.org/plugins/auto-upload-images/\" title=\"Auto Upload Images\" rel=\"noopener nofollow\" target=\"_blank\">Auto Upload Images</a> plugin. For more details, see our step by step guide on <a href=\"http://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\" title=\"Step by Step Guide to Install a WordPress Plugin for Beginners\">how to install a WordPress plugin</a>.</p>\n<p>Upon activation, you need to update the posts containing the external images. This update will trigger the plugin to fetch and store the external images in the article. </p>\n<p>You can also bulk update all articles at once to quickly import all images. For detailed instructions, see our step by step tutorial on <a href=\"https://www.wpbeginner.com/plugins/how-to-import-external-images-in-wordpress/\" title=\"How to Import External Images in WordPress\">how to import external images in WordPress</a>. </p>\n<h4>Step 5. Setting up Redirects</h4>\n<p>If your Medium stories had a medium.com URL, then you cannot setup redirects.</p>\n<p>If you were using a custom domain for your Medium publication, then you can set up custom redirects in WordPress.</p>\n<p>First, you will need to get all URLs of your Medium articles and save them in a text file. After that, you need to start setting up redirects for all your articles.</p>\n<p>There are multiple ways to set up redirects in WordPress. You can follow the instructions in our <a href=\"https://www.wpbeginner.com/beginners-guide/beginners-guide-to-creating-redirects-in-wordpress/\" title=\"Beginner’s Guide to Creating Redirects in WordPress\">beginner’s guide to creating redirects in WordPress</a> for detailed instructions.</p>\n<h4>Step 6. Installing a WordPress Theme</h4>\n<p>By default, WordPress comes with a pretty decent looking design for your website. However, you can change it to anything else that you like. There are thousands of <a href=\"https://www.wpbeginner.com/beginners-guide/decide-premium-free-wordpress-themes/\" title=\"Free vs Premium WordPress Themes (Pros and Cons)\">free and premium WordPress themes</a> that you can use to customize your website. </p>\n<p>Following are our expert picks of the best WordPress themes in various categories: </p>\n<ul>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-free-wordpress-blog-themes/\" title=\"59 Best Free WordPress Blog Themes for 2018\">Best free WordPress blog themes</a></li>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-minimalist-wordpress-themes-for-writers/\" title=\"26 Best Minimalist WordPress Themes for Writers\">Best WordPress themes for writers</a></li>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-wordpress-themes-for-artists/\" title=\"29 Best WordPress Themes for Artists (2017)\">Best WordPress themes for artists</a></li>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-medium-style-wordpress-themes/\" title=\"24 Best Medium-Style WordPress Themes\">Best Medium-like WordPress themes</a></li>\n</ul>\n<p>After you have chosen a theme, you can follow our guide on <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-install-a-wordpress-theme/\" title=\"Beginners Guide: How to Install a WordPress Theme\">how to install a WordPress theme</a> to set it up.  </p>\n<h4>Step 7. Installing Essential WordPress Plugins</h4>\n<p>Plugins are like apps for your WordPress blog. They allow you to add new features and extend functionality of your WordPress site. To learn more, see our article about <a href=\"https://www.wpbeginner.com/beginners-guide/what-are-wordpress-plugins-how-do-they-work/\" title=\"What Are WordPress Plugins? And How Do They Work?\">what are WordPress plugins</a> and how do they work. </p>\n<p>There are thousands of free and <a href=\"https://www.wpbeginner.com/beginners-guide/when-is-it-worth-it-to-buy-premium-wordpress-plugins-explained/\" title=\"When is it Worth It to Buy Premium WordPress Plugins? (Explained)\">premium WordPress plugins</a> that you can install on your website. At the time of writing this article, WordPress.org plugin directory alone had more than 55,000 thousand free plugins. </p>\n<p>With this in mind, how do you know if you need a WordPress plugin? More importantly, how do you find out the best WordPress plugin for the job? </p>\n<p>Well, there are some WordPress plugins that are made for specific websites. For example, <a href=\"https://www.wpbeginner.com/plugins/best-wordpress-ecommerce-plugins-compared/\" title=\"5 Best WordPress Ecommerce Plugins Compared – 2018\">eCommerce plugins</a>, <a href=\"https://www.wpbeginner.com/plugins/5-best-wordpress-membership-plugins-compared/\" title=\"5 Best WordPress Membership Plugins (Compared) – 2018\">membership plugins</a>, or <a href=\"https://www.wpbeginner.com/plugins/best-wordpress-lms-plugins-compared/\" title=\"5 Best WordPress LMS Plugins Compared (Pros and Cons)\">LMS plugins</a>. </p>\n<p>Then there are plugins that almost every website needs. Let&#8217;s install some of those plugins on your new WordPress site. </p>\n<ul>\n<li><a href=\"https://wordpress.org/plugins/wpforms-lite/\" title=\"WPForms Lite\" rel=\"noopener nofollow\" target=\"_blank\">WPForms Lite</a> &#8211; This is the free version of <a href=\"https://wpforms.com\" title=\"WPForms\">WPForms</a> which is the <a href=\"https://www.wpbeginner.com/plugins/5-best-contact-form-plugins-for-wordpress-compared/\" title=\"5 Best Contact Form Plugins for WordPress Compared\">best WordPress contact form plugin</a> on the market. It allows you to easily add beautiful forms to your website. </li>\n<li><a href=\"https://wordpress.org/plugins/google-analytics-for-wordpress/\" title=\"MonsterInsights\" rel=\"noopener nofollow\" target=\"_blank\">MonsterInsights Lite</a> &#8211; This free version of popular <a href=\"https://www.monsterinsights.com\" title=\"MonsterInsights\">MonsterInsights</a> plugin helps you <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-install-google-analytics-in-wordpress/\" title=\"How to Install Google Analytics in WordPress for Beginners\">install Google Analytics in WordPress</a>. It also shows you beautiful reports about your most popular articles, where your users are coming from, and more. </li>\n<li><a href=\"https://wordpress.org/plugins/wordpress-seo/\" title=\"Yoast SEO\" rel=\"noopener nofollow\" target=\"_blank\">Yoast SEO</a> &#8211; This is the <a href=\"https://www.wpbeginner.com/showcase/9-best-wordpress-seo-plugins-and-tools-that-you-should-use/\" title=\"9 Best WordPress SEO Plugins and Tools That You Should Use\">best WordPress SEO plugin</a> on the market. It helps you <a href=\"https://www.wpbeginner.com/wordpress-seo/\" title=\"Ultimate WordPress SEO Guide for Beginners (Step by Step)\">improve WordPress SEO </a>and get more visitors from search engines. </li>\n</ul>\n<p>Need more plugin recommendations? Check out our pick of the <a href=\"https://www.wpbeginner.com/showcase/24-must-have-wordpress-plugins-for-business-websites/\" title=\"24 Must Have WordPress Plugins for Business Websites in 2018\">essential WordPress plugins</a> every website should have. </p>\n<h4>Step 8. Learning WordPress</h4>\n<p>WordPress is quite easy to use. However, as a new user, you may need some help to familiarize yourself with the platform. </p>\n<p>This is where WPBeginner can help. We are the #1 WordPress resource site with thousands of tutorials, guides, and videos created specifically for beginners, small businesses, and non-techy users.</p>\n<p>Following are some of the helpful resources that you can from WPBeginner (all of them are completely free).  </p>\n<ul>\n<li><a href=\"https://www.wpbeginner.com/blog/\">WPBeginner Blog</a> – The central place for all our WordPress tutorials and guides.</li>\n<li><a href=\"https://www.wpbeginner.com/glossary/\">WPBeginner Dictionary</a> – Our WordPress glossary is the best place to familiarize yourself with the WordPress lingo</li>\n<li><a href=\"http://videos.wpbeginner.com/\">WPBeginner Videos</a> – New WordPress users can start with these 23 videos to master WordPress.</li>\n<li><a href=\"http://youtube.com/wpbeginner?sub_confirmation=1\" title=\"WPBeginner on YouTube\" rel=\"noopener nofollow\" target=\"_blank\">WPBeginner on YouTube</a> – Need more video instructions? Subscribe to our YouTube channel with more than 120,000 subscribers and 11 Million+ views.</li>\n<li><a href=\"https://www.wpbeginner.com/blueprint/\" title=\"WPBeginner’s Blueprint\">WPBeginner Blueprint</a> – Check out plugins, tools, and services we use on WPBeginner.</li>\n<li><a href=\"https://www.wpbeginner.com/deals/\" title=\"WPBeginner Deals\">WPBeginner Deals</a> – Exclusive discounts on WordPress products and services for WPBeginner users.</li>\n</ul>\n<p>We hope this article helped you learn how to properly move from Medium to WordPress. You may also want to see our ultimate guide on <a href=\"https://www.wpbeginner.com/wordpress-seo/\" title=\"Ultimate WordPress SEO Guide for Beginners (Step by Step)\">how to improve your WordPress SEO</a>. </p>\n<p>If you liked this article, then please subscribe to our <a href=\"http://youtube.com/wpbeginner?sub_confirmation=1\" title=\"WPBeginner on YouTube\" target=\"_blank\" rel=\"nofollow\">YouTube Channel</a> for WordPress video tutorials. You can also find us on <a href=\"http://twitter.com/wpbeginner\" title=\"WPBeginner on Twitter\" target=\"_blank\" rel=\"nofollow\">Twitter</a> and <a href=\"https://www.facebook.com/wpbeginner\" title=\"WPBeginner on Facebook\" target=\"_blank\" rel=\"nofollow\">Facebook</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://www.wpbeginner.com/wp-tutorials/how-to-properly-move-from-medium-to-wordpress/\">How to Properly Move from Medium to WordPress</a> appeared first on <a rel=\"nofollow\" href=\"https://www.wpbeginner.com\">WPBeginner</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:91:\"https://www.wpbeginner.com/wp-tutorials/how-to-properly-move-from-medium-to-wordpress/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"7\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:44:\"12 Best Free Website Hosting Compared (2019)\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"https://www.wpbeginner.com/showcase/best-free-website-hosting-compared/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"https://www.wpbeginner.com/showcase/best-free-website-hosting-compared/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 14 Dec 2018 13:15:19 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:8:\"Showcase\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:25:\"best free website hosting\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:16:\"free web hosting\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:20:\"free website hosting\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:15:\"website hosting\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://www.wpbeginner.com/?p=58337\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:635:\"<p>Looking for a free web hosting service to start your website? We get emails from readers on a regular basis asking if there&#8217;s a way to get free website hosting. And the answer is YES. We all love free things in life, but it&#8217;s important&#8230;&#160;<strong><a href=\"https://www.wpbeginner.com/showcase/best-free-website-hosting-compared/\">Read More &#187;</a></strong></p>\n<p>The post <a rel=\"nofollow\" href=\"https://www.wpbeginner.com/showcase/best-free-website-hosting-compared/\">12 Best Free Website Hosting Compared (2019)</a> appeared first on <a rel=\"nofollow\" href=\"https://www.wpbeginner.com\">WPBeginner</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Editorial Staff\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:21631:\"<p>Looking for a free web hosting service to start your website? We get emails from readers on a regular basis asking if there&#8217;s a way to get free website hosting. And the answer is YES. We all love free things in life, but it&#8217;s important to remember not all free things are created equal. In this article, we will compare the best free (and almost free) website hosting services with their pros and cons.</p>\n<p><img title=\"Best free website hosting compared\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2018/12/freewebsitehosting.png\" alt=\"Best free website hosting compared\" width=\"550\" height=\"340\" class=\"alignnone size-full wp-image-58395\" /></p>\n<h4>The &#8220;Truth&#8221; About Free Website Hosting Service</h4>\n<p><a href=\"https://www.wpbeginner.com/guides/\" title=\"How to Make a Website – Step by Step Guide\">Building a website</a> has become the most important way to get your message out in the world. However, when you are first starting, you may not want to spend money on making a website. </p>\n<p>Most people simply want something quick, easy, and free. </p>\n<p>There are tons of free website hosting companies in the market. But as a smart consumer, you should know that there is no such thing as free lunch. </p>\n<p>There is always a catch.</p>\n<p>Running a website hosting service costs a lot of money. </p>\n<p>The companies offering free website hosting need to make money somehow, or they will go out of business.</p>\n<p>They do that by either displaying ads on your website, limiting your website traffic, locking you in their platform, and/or upselling their other services. Some of the ads displayed may not represent your brand well, and it can even be your competitor&#8217;s advertisement.</p>\n<p>Except for a test website, it&#8217;s almost always a bad idea to use free website hosting. We have <a href=\"https://www.wpbeginner.com/opinion/36-reasons-why-having-a-free-website-is-a-bad-idea/\" title=\"36 Reasons Why Having a “Free Website” is a Bad Idea\">36 reasons why free websites are bad idea</a>, specially for businesses.</p>\n<p>But what if you&#8217;re on a small budget?</p>\n<p>Maybe you&#8217;re a non-profit who is looking for free WordPress hosting or maybe you want free website hosting for students. Don&#8217;t worry, we have a solution for you in this article.</p>\n<p>As the largest free WordPress resource site for beginners, it is our job to highlight the fine-prints, so you&#8217;re making the RIGHT decision.</p>\n<p>Every free website hosting company in our list guarantees that you can keep your website for free as long as you want. However we recommend anyone who is serious about their website to strongly consider using a low-cost full-featured website hosting provider.</p>\n<p>Majority of these <a href=\"https://www.wpbeginner.com/the-truth-about-shared-wordpress-web-hosting/\" title=\"The Truth About Best Shared WordPress Web Hosting\">shared hosting companies</a> will give you a free domain, free emails, free SSL, eCommerce functionality, and a lot more for $2.75 &#8211; $5 per month. </p>\n<p>That&#8217;s basically &#8220;almost free&#8221; considering you will make a lot more money from your website.</p>\n<p>Paying this small amount gives you access to expert support and overall better service. Most importantly, it gives you complete control and ownership of your website. </p>\n<p>That being said, let&#8217;s take a look at some of the best almost-free website hosting companies first. After that, we will look at the best free website hosting companies.</p>\n<h2>Best Value &#8220;Almost-Free&#8221; Website Hosting</h2>\n<p>These providers are highly-reputed and are a great place for small businesses to start their website. </p>\n<p>Some of them are even officially recommended by WordPress, WPBeginner, and other large organizations because they have demonstrated high level of quality and service.</p>\n<p>If you are serious about your website, then we recommend you to start your website with one of these 5 providers.</p>\n<h4><a title=\"Bluehost\" href=\"https://www.wpbeginner.com/refer/bluehost/\" rel=\"nofollow\" target=\"_blank\">1. Bluehost</a></h4>\n<p><a title=\"Bluehost\" href=\"https://www.wpbeginner.com/refer/bluehost/\" rel=\"nofollow\" target=\"_blank\"><img title=\"Bluehost\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/bluehost.png\" alt=\"Bluehost\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58339\" /></a></p>\n<p><a rel=\"nofollow\" target=\"_blank\" title=\"Bluehost\" href=\"https://www.wpbeginner.com/refer/bluehost/\" data-shortcode=\"true\">Bluehost</a> is one of the largest hosting companies in the world. They are an officially recommended WordPress hosting provider. </p>\n<p>They have agreed to offer WPBeginner users a free domain name, free SSL certificate, and 63% discount on web hosting. Basically, you can get started for $2.75 per month. </p>\n<p>Their hosting plans include <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-create-a-free-business-email-address-in-5-minutes-step-by-step/\" title=\"How to Create a Free Business Email Address in 5 Minutes (Step by Step)\">free branded email addresses</a>, unlimited disk space, free SSL certificate, 1-click WordPress install, and more.  </p>\n<p>To learn more about Bluehost, see our complete <a href=\"https://www.wpbeginner.com/hosting/bluehost/\" title=\"Bluehost Review - Honest Look at Speed &amp; Uptime\">Bluehost review</a> where we evaluated their hosting performance, features, and services. </p>\n<h4><a title=\"SiteGround\" href=\"https://www.wpbeginner.com/refer/siteground/\" rel=\"nofollow\" target=\"_blank\">2. SiteGround</a></h4>\n<p><a title=\"SiteGround\" href=\"https://www.wpbeginner.com/refer/siteground/\" rel=\"nofollow\" target=\"_blank\"><img title=\"SiteGround\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/siteground.jpg\" alt=\"SiteGround\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58340\" /></a></p>\n<p><a rel=\"nofollow\" target=\"_blank\" title=\"SiteGround\" href=\"https://www.wpbeginner.com/refer/siteground/\" data-shortcode=\"true\">Siteground</a> is another great alternative to free website hosting. They are known for their awesome support and blazingly fast performance. SiteGround is also an officially recommended WordPress hosting provider. </p>\n<p>They are offering WPBeginner users 60% discount on hosting. Basically, you&#8217;ll be able to get started for $3.95 per month. </p>\n<p>SiteGround&#8217;s hosting plans come with a website builder, 1-click installer for WordPress and Joomla, unlimited email addresses, and more. </p>\n<p>SiteGround have a special offer for Teachers and Students. You can get <a href=\"https://www.wpbeginner.com/refer/siteground-student-hosting/\" title=\"Student Web Hosting by SiteGround\" rel=\"noopener nofollow\" target=\"_blank\">Student Web Hosting</a> for only $1.99 per month.</p>\n<p>Teachers can use SiteGround to enroll in a free hosting plan for educators.</p>\n<p>For more details, see our thorough <a href=\"https://www.wpbeginner.com/hosting/siteground/\" title=\"SiteGround Reviews by Users and Our Experts\">SiteGround review</a> where we ran actual tests to measure their performance and uptime.  </p>\n<h4><a title=\"HostGator\" href=\"https://www.wpbeginner.com/refer/hostgator/\" rel=\"nofollow\" target=\"_blank\">3. HostGator</a></h4>\n<p><a title=\"HostGator\" href=\"https://www.wpbeginner.com/refer/hostgator/\" rel=\"nofollow\" target=\"_blank\"><img title=\"HostGator\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/hostgator.png\" alt=\"HostGator\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58341\" /></a></p>\n<p><a rel=\"nofollow\" target=\"_blank\" title=\"HostGator\" href=\"https://www.wpbeginner.com/refer/hostgator/\" data-shortcode=\"true\">HostGator</a> is one of the top hosting service providers in the market. They have been around since 2002 and gradually grew to become one of the largest hosting companies in the world. </p>\n<p>Their incredible growth can be credited to their low-price, reliable web hosting, and quality customer service.</p>\n<p>Their hatchling plan starts from $2.64 / month. Each plan includes unlimited email addresses, <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-get-a-free-ssl-certificate-for-your-wordpress-website/\" title=\"How to Get a Free SSL Certificate for Your WordPress Website\">free HTTPS / SSL</a>, unmetered bandwidth and diskspace. </p>\n<p>Our founder and CEO, Syed Balkhi, is a <a title=\"HostGator\" href=\"https://www.wpbeginner.com/refer/hostgator/\" rel=\"nofollow\" target=\"_blank\">HostGator</a> customer since 2007. WPBeginner itself is hosted with HostGator&#8217;s dedicated server and we are very happy with their performance.</p>\n<p>For more details, see our <a href=\"https://www.wpbeginner.com/hosting/hostgator/\" title=\"HostGator review\">HostGator review</a> with pros and cons, and detailed performance tests. </p>\n<h4><a title=\"DreamHost\" href=\"https://www.wpbeginner.com/refer/dreamhost/\" rel=\"nofollow\" target=\"_blank\">4. DreamHost</a></h4>\n<p><a title=\"DreamHost\" href=\"https://www.wpbeginner.com/refer/dreamhost/\" rel=\"nofollow\" target=\"_blank\"><img title=\"DreamHost\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/dreamhost.png\" alt=\"DreamHost\" width=\"550\" height=\"280\" class=\"alignnone size-full wp-image-58342\" /></a></p>\n<p><a rel=\"nofollow\" target=\"_blank\" title=\"DreamHost\" href=\"https://www.wpbeginner.com/refer/dreamhost/\" data-shortcode=\"true\">DreamHost</a> is one of the oldest web hosting companies, started in 1997. They currently host more than 1.5 million websites. </p>\n<p>Their starter plan costs $2.59 per month. It includes a free domain name, free domain privacy, free SSL, unlimited emails, 1-click installers for <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-choose-the-best-website-builder/\" title=\"How to Choose the Best Website Builder in 2018 (Compared)\">popular website builders</a>, and more. </p>\n<p>To learn more, see our complete <a href=\"https://www.wpbeginner.com/hosting/dreamhost/\" title=\"Dreamhost review\">DreamHost review</a> where we evaluated their services, support, and performance. </p>\n<h4><a title=\"Just Host\" href=\"https://www.wpbeginner.com/refer/justhost/\" rel=\"nofollow\" target=\"_blank\">5. Just Host</a></h4>\n<p><a title=\"Just Host\" href=\"https://www.wpbeginner.com/refer/justhost/\" rel=\"nofollow\" target=\"_blank\"><img title=\"JustHost\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2018/12/justhost.png\" alt=\"Just Host\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58343\" /></a></p>\n<p>Just Host is another low-cost alternative to the free website hosting companies. They offer shared hosting plans perfect for creating a starter website. </p>\n<p>You can basically get started for $3.95 per month. Each plan comes with a free domain name, unlimited email addresses, multiple websites, and 1-click install for to <a href=\"https://www.wpbeginner.com/glossary/content-management-system-cms/\" title=\"Content Management System (CMS)\">CMS software</a> like WordPress.</p>\n<p>To learn more, take a look at our complete <a href=\"https://www.wpbeginner.com/hosting/justhost/\" title=\"Just Host Review by our Experts\">Just Host review</a> with pros and cons. </p>\n<h2>Best Free Hosting Sites</h2>\n<p>While free is tempting, it&#8217;s always limited when it comes to website hosting. With that in mind, we have hand-picked the best free website hosting sites that you can use to get started.</p>\n<p>These hosting sites are 100% free for you to use.</p>\n<p>If you&#8217;re a serious about your website, we strongly recommend you to read our comprehensive guide on <a href=\"https://www.wpbeginner.com/wordpress-hosting/\" title=\"How to Choose the Best WordPress Hosting?\">how to choose the best WordPress hosting</a> before making your final decision.</p>\n<p>With that said, here are the best free hosting companies in the world.</p>\n<h4><a title=\"WordPress.com\" href=\"https://www.wpbeginner.com/refer/wordpresscom/\" rel=\"nofollow\" target=\"_blank\">6. WordPress.com</a></h4>\n<p><a title=\"WordPress.com\" href=\"https://www.wpbeginner.com/refer/wordpresscom/\" rel=\"nofollow\" target=\"_blank\"><img title=\"WordPress.com\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/wpcom.png\" alt=\"WordPress.com\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58344\" /></a></p>\n<p>WordPress.com is a popular free hosting platform that offers a unique flavor of WordPress. Not to be confused with the WordPress.org, which is home of the well-known self-hosted website builder, WordPress. </p>\n<p>WordPress.com offer a free website hosting plan which gives you a WordPress.com subdomain (i.e. yourwebsite.wordpress.com ), 3 GB storage, and access to free templates for your website. </p>\n<p>This free plan displays ads on your website. You can upgrade to their paid plans to get rid of ads, use a custom domain name, and access additional features. At which point any of the &#8220;almost free hosting&#8221; services we mentioned above are the better option.</p>\n<p>To learn more, see our comparison of <a href=\"https://www.wpbeginner.com/beginners-guide/self-hosted-wordpress-org-vs-free-wordpress-com-infograph/\" title=\"WordPress.com vs WordPress.org – Which is Better? (Comparison Chart)\">WordPress.com vs WordPress.org</a> with pros and cons of both platforms. </p>\n<h4><a title=\"Wix\" href=\"https://www.wpbeginner.com/refer/wix/\" rel=\"nofollow\" target=\"_blank\">7. Wix</a></h4>\n<p><a title=\"Wix\" href=\"https://www.wpbeginner.com/refer/wix/\" rel=\"nofollow\" target=\"_blank\"><img title=\"Wix\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2018/12/wix.png\" alt=\"Wix\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58345\" /></a></p>\n<p>Wix is another fully-hosted website builder that offers free website hosting. </p>\n<p>Like most free hosting services, it is supported by displaying ads and Wix.com branding on your free website. You can get rid of it by upgrading to their paid plans. </p>\n<p>The free plan will give you a Wix.com subdomain, access to website templates, 500 MB of storage and 500 MB of bandwidth. </p>\n<p>For more details, see our comparison of <a href=\"https://www.wpbeginner.com/opinion/wix-vs-wordpress-which-one-is-better-pros-and-cons/\" title=\"Wix vs WordPress – Which One is Better? (Pros and Cons)\">Wix vs WordPress</a> where we break down the pros and cons of both platforms. </p>\n<h4><a title=\"Weebly\" href=\"https://www.wpbeginner.com/refer/weebly/\" rel=\"nofollow\" target=\"_blank\">8. Weebly</a></h4>\n<p><a title=\"Weebly\" href=\"https://www.wpbeginner.com/refer/weebly/\" rel=\"nofollow\" target=\"_blank\"><img title=\"Weebly\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2018/12/weebly.png\" alt=\"Weebly\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58346\" /></a> </p>\n<p><a rel=\"nofollow\" target=\"_blank\" title=\"Weebly\" href=\"https://www.wpbeginner.com/refer/weebly/\" data-shortcode=\"true\">Weebly</a> is another popular free website builder that you can use. It comes with an intuitive drag and drop builder and ready-made templates to create a website. </p>\n<p>Their free plan includes a Weebly subdomain, 500 MB of storage, and displays Weebly ads. You can upgrade to their paid plans to get rid of ads, get more storage, and unlock features. </p>\n<p>You may want to see our detailed comparison of <a href=\"https://www.wpbeginner.com/opinion/wordpress-vs-weebly-which-one-is-better-comparison/\" title=\"WordPress vs Weebly – Which one is better? (Comparison)\">Weebly vs WordPress</a> which shows the advantages and disadvantages of both website platforms. </p>\n<h4><a title=\"GoDaddy Website Builder\" href=\"https://www.wpbeginner.com/refer/godaddy-website-builder/\" rel=\"nofollow\" target=\"_blank\">9. GoDaddy Website Builder</a></h4>\n<p><a title=\"GoDaddy Website Builder\" href=\"https://www.wpbeginner.com/refer/godaddy-website-builder/\" rel=\"nofollow\" target=\"_blank\"><img title=\"GoDaddy Website Builder\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2018/12/godaddywebsitebuilder.png\" alt=\"GoDaddy Website Builder\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58347\" /></a></p>\n<p><a rel=\"nofollow\" target=\"_blank\" title=\"Godaddy\" href=\"https://www.wpbeginner.com/refer/godaddy/\" data-shortcode=\"true\">GoDaddy</a> is the world&#8217;s largest <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-choose-the-best-domain-registrar/\" title=\"How to Choose the Best Domain Registrar in 2018 (Compared)\">domain name registrar</a> and a popular hosting company. </p>\n<p>They offer GoDaddy website builder which is a drag and drop website builder with ready-made templates. It is available as a 30-day free trial after which you can upgrade to one of their paid plans starting from $5.99 per month. </p>\n<p>It is cheap and very easy to use. However, once your website grows you may find GoDaddy Website Builder to be quite limited in features. It is also difficult to <a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-move-godaddy-website-builder-site-to-wordpress/\" title=\"How to Move GoDaddy Website Builder Site to WordPress\">move GoDaddy Website Builder to WordPress</a> or any other website platform.  </p>\n<h4><a href=\"https://www.squarespace.com/\" title=\"Squarespace\" rel=\"noopener nofollow\" target=\"_blank\">10. Squarespace</a></h4>\n<p><a href=\"https://www.squarespace.com/\" title=\"Squarespace\" rel=\"noopener nofollow\" target=\"_blank\"><img title=\"Squarespace\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2018/12/squarespace-logo.jpg\" alt=\"Squarespace\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58348\" /></a></p>\n<p>Squarespace is a popular website builder platform that requires no technical skills. It offers beautiful professional templates and an intuitive drag and drop interface. </p>\n<p>They offer a restricted 14-day free website hosting trial. This gives you access to their website builder so you can try its features. Your website will not be public unless you subscribe to a paid plan. </p>\n<p>Paid plans start at $12 per month. Each plan comes with a free custom domain, unlimited bandwidth, storage, and ready-made website templates. </p>\n<p>To learn more, see our article on <a href=\"https://www.wpbeginner.com/opinion/squarespace-vs-wordpress-which-one-is-better-pros-and-cons/\" title=\"Squarespace vs WordPress – Which one is better? (Pros and Cons)\">Squarespace vs WordPress</a> with pros and cons and feature comparisons. </p>\n<h4><a href=\"https://cloud.google.com/solutions/smb/web-hosting/#solutions\" title=\"Google Cloud Hosting\" rel=\"noopener nofollow\" target=\"_blank\">11. Google Cloud Hosting</a></h4>\n<p><a href=\"https://cloud.google.com/solutions/smb/web-hosting/#solutions\" title=\"Google Cloud Hosting\" rel=\"noopener nofollow\" target=\"_blank\"><img title=\"Google Cloud Logo\"  src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2018/12/googlecloudlogo.png\" alt=\"Google Cloud Logo\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58403\" /></a></p>\n<p>Google offers a wide variety of free website hosting solutions for small businesses and students. It comes with all the powerful features that you can expect from a top-class company like Google.</p>\n<p>Since their plans are based on pay-per-use, it&#8217;s an easy place for students to get started. Google offers $300 free credit for new customers which is sufficient to last you for a whole year, if you&#8217;re just looking for a free website hosting for test sites.</p>\n<p>You can use Google Cloud to host any type of website, and they even offer WordPress on Google Compute Engine.</p>\n<h4><a href=\"https://aws.amazon.com/free/\" title=\"AWS\" rel=\"noopener nofollow\" target=\"_blank\">12. Amazon Web Services (AWS)</a></h4>\n<p><a href=\"https://aws.amazon.com/free/\" title=\"AWS\" rel=\"noopener nofollow\" target=\"_blank\"><img title=\"AWS Logo\"  src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2018/12/awslogo.png\" alt=\"AWS Logo\" width=\"550\" height=\"300\" class=\"alignnone size-full wp-image-58404\" /></a></p>\n<p>Amazon Web Services also known as AWS powers some of the most popular websites in the world. While it&#8217;s not easy to use for small businesses, it&#8217;s a great platform for students and developers who&#8217;re just getting started.</p>\n<p>Amazon offers 12 months free and always free products on their AWS Free Tier which is by far one of the best free website hosting for testing sites.</p>\n<p>Students can use Amazon&#8217;s powerful platform to learn and master their development skills.</p>\n<p>We hope this article helped you find the best free website hosting service for your next project. You may also want to checkout our guide on <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-create-a-free-business-email-address-in-5-minutes-step-by-step/\" title=\"How to Create a Free Business Email Address in 5 Minutes (Step by Step)\">how to create a free business email address in 5 minutes</a>.</p>\n<p>If you liked this article, then please subscribe to our <a href=\"http://youtube.com/wpbeginner?sub_confirmation=1\" title=\"WPBeginner on YouTube\" target=\"_blank\" rel=\"nofollow\">YouTube Channel</a> for WordPress video tutorials. You can also find us on <a href=\"http://twitter.com/wpbeginner\" title=\"WPBeginner on Twitter\" target=\"_blank\" rel=\"nofollow\">Twitter</a> and <a href=\"https://www.facebook.com/wpbeginner\" title=\"WPBeginner on Facebook\" target=\"_blank\" rel=\"nofollow\">Facebook</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://www.wpbeginner.com/showcase/best-free-website-hosting-compared/\">12 Best Free Website Hosting Compared (2019)</a> appeared first on <a rel=\"nofollow\" href=\"https://www.wpbeginner.com\">WPBeginner</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:76:\"https://www.wpbeginner.com/showcase/best-free-website-hosting-compared/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"17\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:51:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:52:\"How to Install and Setup WordPress Multisite Network\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"https://www.wpbeginner.com/wp-tutorials/how-to-install-and-setup-wordpress-multisite-network/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:102:\"https://www.wpbeginner.com/wp-tutorials/how-to-install-and-setup-wordpress-multisite-network/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 13 Dec 2018 13:00:59 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:4:{i:0;a:5:{s:4:\"data\";s:9:\"Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:34:\"how to install wordpress multisite\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:30:\"multisite network in wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:19:\"wordpress multisite\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"http://www.wpbeginner.com/?p=20409\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:716:\"<p>Do you want to setup a WordPress multisite network? WordPress comes with a built-in capability to create multiple websites using the same WordPress installation. In this article, we will show you how to properly install and setup a WordPress multisite network. Since this is a&#8230;&#160;<strong><a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-install-and-setup-wordpress-multisite-network/\">Read More &#187;</a></strong></p>\n<p>The post <a rel=\"nofollow\" href=\"https://www.wpbeginner.com/wp-tutorials/how-to-install-and-setup-wordpress-multisite-network/\">How to Install and Setup WordPress Multisite Network</a> appeared first on <a rel=\"nofollow\" href=\"https://www.wpbeginner.com\">WPBeginner</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Editorial Staff\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:30589:\"<p>Do you want to setup a WordPress multisite network? WordPress comes with a built-in capability to create multiple websites using the same WordPress installation. In this article, we will show you how to properly install and setup a WordPress multisite network.</p>\n<p><img title=\"How to install and setup WordPress multisite network\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2017/06/wpmultisite-1.png\" alt=\"How to install and setup WordPress multisite network\" width=\"550\" height=\"340\" class=\"alignnone size-full wp-image-44913\" /></p>\n<p>Since this is a comprehensive article, we have added the table of contents for easier navigation:</p>\n<ol>\n<li><a href=\"#multisite\">What is WordPress Multisite?</a></li>\n<li><a href=\"#pros\">Pros of using a WordPress multisite network</a></li>\n<li><a href=\"#cons\">Cons of using a WordPress multisite network</a></li>\n<li><a href=\"#whoneeds\">Who needs a WordPress multisite network?</a></li>\n<li><a href=\"#requirements\">Requirements for a WordPress multisite network</a></li>\n<li><a href=\"#domain\">Choosing a domain structure for your multisite network</a></li>\n<li><a href=\"#wildcard\">Setting up wildcard subdomains</a></li>\n<li><a href=\"#enabling\">Enabling WordPress multisite network feature</a></li>\n<li><a href=\"#settingup\">Setting up your WordPress multisite network</a></li>\n<li><a href=\"#config\">Configuring network settings</a></li>\n<li><a href=\"#registrations\">Opening your multisite network for registrations</a></li>\n<li><a href=\"#newsite\">New site settings</a></li>\n<li><a href=\"#upload\">Upload settings for your multisite network</a></li>\n<li><a href=\"#pluginmenu\">Plugin menu settings</a></li>\n<li><a href=\"#newsites\">Adding new sites to your WordPress multisite network</a></li>\n<li><a href=\"#themesplugins\">Adding themes and plugins to your multisite network</a></li>\n<li><a href=\"#issues\">Troubleshooting WordPress multisite issues</a></li>\n</ol>\n<h4 id=\"multisite\">What is WordPress Multisite Network?</h4>\n<p>A WordPress <a href=\"https://www.wpbeginner.com/glossary/multisite/\" title=\"Multisite (MU)\">Multisite</a> network allows you to run and manage multiple WordPress sites or blogs from a single WordPress installation.</p>\n<p>It enables you to create new sites instantly and manage them using the same username and password. You can even allow other users to signup and create their own blogs on your domain.</p>\n<p>The WordPress multisite network comes with advanced settings that you can use to customize each website / blog on your network.</p>\n<p><strong id=\"pros\">Pros of Using a WordPress Multisite Network</strong></p>\n<p>In many situations, a WordPress multisite network can be more useful than managing multiple standalone WordPress sites. Here are some of the advantages of using a WordPress multisite network:</p>\n<ol>\n<li>As the network administrator, you can easily manage multiple sites from a single dashboard.<br />\n.</li>\n<li>Each site on the network can have its own admins. The site admins will have the capabilities to manage only their own website.</li>\n<li>You can install plugins / themes and activate them for multiple sites with one download.</li>\n<li>Multisite network also makes it easier for you to manage updates. You only need to update your WordPress, plugins, or themes on one “master” install.</li>\n</ol>\n<p><strong id=\"cons\">Cons of Using a WordPress Multisite Network</strong></p>\n<p>Creating a WordPress multisite network is not always helpful in managing multiple sites. Here are some of the disadvantages that you should keep in mind before setting up a multisite network.</p>\n<ol>\n<li>All sites on the network share the same resources. This means that when your network is down, all other sites go down as well.</li>\n<li>It&#8217;s not easy to manage traffic and server resources for the beginner level users. In case, one of your websites gets unexpected traffic, then it will affect all other websites on the network.</li>\n<li>If one website gets hacked, then this means all sites on your network will get hacked.</li>\n<li>Some WordPress plugins may not work well on a multisite network.</li>\n<li>WordPress multisite network is not properly supported by all web hosting providers which limits your options. We will talk more about this later in the article.</li>\n</ol>\n<h4 id=\"whoneeds\">Who needs a WordPress multisite network?</h4>\n<p>Just because you manage multiple WordPress websites does not mean you need to start using a multisite network.</p>\n<p>There are third party tools like <a href=\"https://www.wpbeginner.com/refer/managewp/\" title=\"ManageWP\" rel=\"noopener nofollow\" target=\"_blank\">ManageWP</a> or <a href=\"https://www.wpbeginner.com/refer/infinitewp/\" title=\"InfiniteWP\" rel=\"noopener nofollow\" target=\"_blank\">InfiniteWP</a> that you can use to manage all your WordPress sites under one roof.</p>\n<p>Here are some scenarios when creating a multisite network makes sense:</p>\n<ol>\n<li>A magazine website with different sections managed by different teams.</li>\n<li>A <a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-make-a-small-business-website-step-by-step/\" title=\"How to Make a Small Business Website – Step by Step (2018)\">business website</a> with sub-sites for different locations and branches.</li>\n<li>Government or non-profit websites can use WordPress multisite for different departments, locations, and regions.</li>\n<li>Your own network of blogs running on multiple subdomains.</li>\n<li>Schools and colleges allowing students to create their own blogs on school servers.</li>\n</ol>\n<h4 id=\"requirements\">Requirements for a WordPress Multisite Network</h4>\n<p>All websites on a WordPress multisite network share the same server resources. This means that the most important thing you will need is a <a href=\"https://www.wpbeginner.com/wordpress-hosting/\" title=\"How to Choose the Best WordPress Hosting?\">good WordPress hosting</a>.</p>\n<p>If you are planning on having just a couple of websites with low traffic, then you can probably get away with <a href=\"https://www.wpbeginner.com/the-truth-about-shared-wordpress-web-hosting/\" title=\"The Truth About Best Shared WordPress Web Hosting\">shared hosting</a>.</p>\n<p>However, due to the nature of multisite network, you&#8217;d need <a href=\"https://www.wpbeginner.com/opinion/do-you-really-need-a-vps-best-wordpress-vps-hosting-compared-2017/\" title=\"Do You Really Need a VPS? Best WordPress VPS Hosting Compared – 2017\">VPS hosting</a> or a dedicated server as your sites grow.</p>\n<p>We recommend <a href=\"https://www.wpbeginner.com/refer/bluehost/\" title=\"Bluehost\" rel=\"noopener nofollow\" target=\"_blank\">Bluehost</a> because they offer both shared hosting and VPS/Dedicated servers, and they are also one of the official WordPress hosting partners.</p>\n<p>If you&#8217;re looking for an alternative, then <a href=\"https://www.wpbeginner.com/refer/siteground/\" title=\"SiteGround\" rel=\"noopener nofollow\" target=\"_blank\">SiteGround</a> and <a title=\"WPEngine\" href=\"https://www.wpbeginner.com/refer/wpengine/\" rel=\"nofollow\" target=\"_blank\" >WP Engine</a> provide excellent service for WordPress multisite network.</p>\n<p>Apart from web hosting, you will need the basic knowledge of <a href=\"https://www.wpbeginner.com/how-to-install-wordpress/\" title=\"How to Install WordPress – Complete WordPress Installation Tutorial\">how to install WordPress</a> and <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-use-ftp-to-upload-files-to-wordpress-for-beginners/\" title=\"How to use FTP to upload files to WordPress for Beginners\">editing files using FTP</a>.</p>\n<h4 id=\"domain\">Choosing a Domain Structure for your Multisite Network</h4>\n<p>On a WordPress multisite network, you can add new sites using either <a href=\"https://www.wpbeginner.com/glossary/subdomain/\" title=\"Subdomain\">subdomains</a> or sub-directories.</p>\n<p><strong>Example of subdomain:</strong><br />\nhttp://site1.example.com</p>\n<p><strong>Example of sub-directory:</strong><br />\nhttp://example.com/site1/</p>\n<p>If you choose subdomains, then you will have to configure wildcard subdomains on your server. We will show you how to do that in the next step.</p>\n<p>On the other hand, if you choose sub-directories or path based URLs for sites on your network, then the only thing you need to do is enable <a href=\"https://www.wpbeginner.com/wp-tutorials/seo-friendly-url-structure-for-wordpress/\" title=\"What is a SEO Friendly URL Structure in WordPress\">pretty permalinks</a> on your root site.</p>\n<h4 id=\"wildcard\">Setting Up Wildcard Subdomains</h4>\n<p>If you decide to use subdomains for websites on your multisite network, then you will need to setup wildcard subdomains for your multisite network.</p>\n<p>To do that, first you need to login to your WordPress hosting account’s <a href=\"https://www.wpbeginner.com/glossary/cpanel/\" title=\"cPanel\">cPanel</a> dashboard. After that you need to scroll down to the &#8216;Domains&#8217; section and then click on &#8216;Subdomains&#8217;.</p>\n<p><img title=\"Subdomains in cPanel dashboard\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/subdomains-cpanel.png\" alt=\"Subdomains in cPanel dashboard\" width=\"550\" height=\"244\" class=\"alignnone size-full wp-image-44884\" /></p>\n<p><strong>Note:</strong> Depending on your web hosting service, your cPanel dashboard may look slightly different than the screenshot above. This screenshot is from the cPanel on <a href=\"https://www.wpbeginner.com/refer/bluehost/\" title=\"Bluehost\" rel=\"noopener nofollow\" target=\"_blank\">Bluehost</a>.</p>\n<p>On the next page, you need to enter the * sign in the subdomain field and select your main domain from the drop-down menu.</p>\n<p><img title=\"Adding wildcard subdomain\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2017/06/wildcard-subdomain.png\" alt=\"Adding wildcard subdomain\" width=\"550\" height=\"293\" class=\"alignnone size-full wp-image-44885\" /></p>\n<p>Cpanel will automatically detect the document root and will display it in the field below. Now click on the ‘Create’ button to add your wildcard subdomain.</p>\n<h4 id=\"enabling\">Enabling WordPress Multisite Network Feature</h4>\n<p>Multisite Network feature comes built-in with each WordPress installation. All you need to do is <a href=\"https://www.wpbeginner.com/how-to-install-wordpress/\" title=\"How to Install WordPress – Complete WordPress Installation Tutorial\">install and setup WordPress</a> like you normally would. After that, you just need to enable the multisite feature.</p>\n<p>You can also enable the multisite feature on any existing WordPress site. Before you enable multisite, don’t forget to create a <a href=\"https://www.wpbeginner.com/plugins/7-best-wordpress-backup-plugins-compared-pros-and-cons/\" title=\"7 Best WordPress Backup Plugins Compared (Pros and Cons)\">complete backup of your WordPress site</a>.</p>\n<p>To enable Multisite, connect to your site using a <a href=\"https://www.wpbeginner.com/showcase/6-best-ftp-clients-for-wordpress-users/\" title=\"6 Best FTP Clients for Mac and Windows WordPress Users\">FTP client</a> or cPanel file manager, and open the <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-edit-wp-config-php-file-in-wordpress/\" title=\"How to Edit wp-config.php File in WordPress\">wp-config.php file</a> for editing.</p>\n<p>You need to add the following code to your wp-config.php file just before the /* That’s all, stop editing! Happy blogging. */ line.</p>\n<pre class=\"brush: php; title: ; notranslate\">\r\n/* Multisite */\r\ndefine( \'WP_ALLOW_MULTISITE\', true );\r\n</pre>\n<p>Once you are done, you can save and upload your wp-config.php file back to the server.</p>\n<p>This code simply enables the multisite feature on your WordPress site. Once enabled, you will still need to setup the multisite network.</p>\n<h4 id=\"settingup\">Setting Up Your WordPress Multisite Network</h4>\n<p>Now that you have successfully enabled the Multisite Network feature on your WordPress site, it is time to set up your network.</p>\n<p>If you are setting up a mutlisite network on an existing WordPress website, then you will need to deactivate all plugins on your site.</p>\n<p>Simply visit the <strong>Plugins &raquo; Installed Plugins</strong> page and select all plugins. You need to select &#8216;Deactivate&#8217; from the &#8216;Bulk Actions&#8217; dropdown menu and then click on the &#8216;Apply&#8217; button.</p>\n<p><img title=\"Deactivate plugins\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2018/12/deactiveplugins.jpg\" alt=\"Deactivate plugins\" width=\"550\" height=\"350\" class=\"alignnone size-full wp-image-58298\" /></p>\n<p>You can now head over to <strong>Tools &raquo; Network Setup</strong> page to configure your multisite network.</p>\n<p><img title=\"WordPress multisite network setup\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/networksetup.png\" alt=\"WordPress multisite network setup\" width=\"550\" height=\"393\" class=\"alignnone size-full wp-image-44888\" /></p>\n<p>On the network setup screen, you will see a notice that you need Apache’s mod_rewrite module installed on your server. This module is installed and enabled on all the <a href=\"https://www.wpbeginner.com/wordpress-hosting/\" title=\"How to Choose the Best WordPress Hosting?\">best WordPress hosting providers</a>.</p>\n<p>The next thing you need to do is to tell WordPress what kind of domain structure you will be using for sites in your network, e.g. Subdomains or Sub-directories.</p>\n<p>After that, you would need to provide a title for your network and make sure that the email address in the Network admin email is correct.</p>\n<p>Click on the install button to continue.</p>\n<p>WordPress will now show you some code that you need to add to your wp-config.php and <a href=\"https://www.wpbeginner.com/beginners-guide/why-you-cant-find-htaccess-file-on-your-wordpress-site/\" title=\"Why You Can’t Find .htaccess File on Your WordPress Site\">.htaccess</a> file respectively.</p>\n<p><img title=\"Add code to wp-config and .htaccess files\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2017/06/networksetup2.png\" alt=\"Add code to wp-config and .htaccess files\" width=\"550\" height=\"424\" class=\"alignnone size-full wp-image-44889\" /></p>\n<p>You can use an FTP client or file manager in the cPanel to copy and paste the code in these two files.</p>\n<p>After that you will need to re-login to your WordPress site to access the multisite network.</p>\n<h4 id=\"config\">Configuring Network Settings</h4>\n<p>Now that you have setup the multisite network, it is time to configure network settings.</p>\n<p>You need to switch to the &#8216;Network Dashboard&#8217; to change network settings, add new sites, and configure other settings.</p>\n<p>When you take your mouse over to the &#8216;My Sites&#8217; menu in the admin toolbar, a flydown popup will appear. Click on the <strong>Network Admin &raquo; Dashboard</strong>.</p>\n<p><img title=\"Network admin dashboard\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2017/06/networkadmin.png\" alt=\"Network admin dashboard\" width=\"550\" height=\"256\" class=\"alignnone size-full wp-image-44890\" /></p>\n<p>This will take you to the multisite network dashboard. You will notice that there are new menu items to manage your multisite network. You will also see a &#8220;Right Now&#8221; dashboard widget that allows you to create a new site and add new users.</p>\n<p><img title=\"Network dashboard\" src=\"https://cdn.wpbeginner.com/wp-content/uploads/2017/06/networkdashboard.png\" alt=\"Network dashboard\" width=\"550\" height=\"338\" class=\"alignnone size-full wp-image-44891\" /></p>\n<p>To configure network settings, you need to click on the &#8216;Settings&#8217; link in the admin sidebar.</p>\n<p>The first option on network settings page is to set your site title and admin email address. These fields will be filled automatically with the network title and admin email you entered during setup.</p>\n<p><img title=\"Network settings\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2017/06/netsettings.png\" alt=\"Network settings\" width=\"550\" height=\"226\" class=\"alignnone size-full wp-image-44892\" /></p>\n<p><strong id=\"registrations\">Opening Your Multisite Network for Registrations</strong></p>\n<p>The &#8216;Registration Settings&#8217; section on the network settings page is probably the most important setting in your network setup.</p>\n<p>By default, both user and site registrations are disabled on the network.</p>\n<p>You can choose to open your site for user registration, or allow existing users to create new sites, or allow both user and site registration.</p>\n<p><img title=\"WordPress multisite registration settings\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/registrationsettings.png\" alt=\"WordPress multisite registration settings\" width=\"550\" height=\"232\" class=\"alignnone size-full wp-image-44893\" /></p>\n<p>If you are opening your multisite network to registration, then you can check the box next to &#8216;Registration Notification&#8217; option to receive email notifications every time a new user or site is registered.</p>\n<p>If you want to allow individual site administrators to add new users on their sites, then you can check the box next to &#8216;Add New Users&#8217; option.</p>\n<p><img title=\"Registration options\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2017/06/registeroptions.png\" alt=\"Registration options\" width=\"550\" height=\"293\" class=\"alignnone size-full wp-image-44894\" /></p>\n<p>Limited Email Registration option allows you to limit site or user registration to email addresses from specific domains. This is particularly useful if you only want to allow people from your own organization to register and create users or sites.</p>\n<p>Similarly, you can also ban certain domains from registration.</p>\n<p><strong id=\"newsite\">New Site Settings</strong></p>\n<p>The &#8216;New Site Settings&#8217; section allows you to configure default options for new sites created on your multisite network. You can modify the welcome emails and the contents of first default post, page, and comment in these settings.</p>\n<p><img title=\"New site settings\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/newsitesettings.png\" alt=\"New site settings\" width=\"550\" height=\"327\" class=\"alignnone size-full wp-image-44895\" /></p>\n<p>As a network administrator, you can change these settings anytime.</p>\n<p><strong id=\"upload\">Upload Settings for Your Multisite Network</strong></p>\n<p>It is important for you to keep an eye on the usage of your server resources. Under the Upload Settings section, you can limit the total amount of space a site can use for uploads.</p>\n<p>The default value is 100 MB which is probably good for at least 100 photo uploads. You can increase or decrease this space depending on how much disk space you have.</p>\n<p><img title=\"Upload settings\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/uploadsettings.png\" alt=\"Upload settings\" width=\"550\" height=\"248\" class=\"alignnone size-full wp-image-44896\" /></p>\n<p>The default upload file types are images, audio, video, and pdf files. You can add <a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-add-additional-file-types-to-be-uploaded-in-wordpress/\" title=\"How to Add Additional File Types to be Uploaded in WordPress\">additional file types</a> if you want like doc, docx, odt, etc.</p>\n<p>After that, you can choose a file size limit, so that users can&#8217;t upload insanely large files to the server.</p>\n<p><strong id=\"pluginmenu\">Plugin Menu Settings</strong></p>\n<p>Next, you can jump to the menu settings. It will allow you to enable the administrative menu for the plugins section on your network sites.</p>\n<p><img title=\"Show or hide plugins menu to site admins\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/pluginmenu.png\" alt=\"Show or hide plugins menu to site admins\" width=\"550\" height=\"191\" class=\"alignnone size-full wp-image-44897\" /></p>\n<p>Enabling this will show plugins menu to respective site admins. They can activate or deactivate a plugin on their individual sites, but they cannot install new plugins.</p>\n<p>Once you are satisfied with all the configuration settings, make sure to click on the &#8216;Save Changes&#8217; button.</p>\n<h4 id=\"newsites\">Adding New Sites to Your WordPress Multisite Network</h4>\n<p>To add a new site to your WordPress multisite network, simply click on &#8216;Sites&#8217; under <strong>My Sites &raquo; Network Admin</strong> menu in the admin toolbar.</p>\n<p><img title=\"Adding new sites to your WordPress multisite network\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/addsites.png\" alt=\"Adding new sites to your WordPress multisite network\" width=\"550\" height=\"199\" class=\"alignnone size-full wp-image-44898\" /></p>\n<p>This will show you a list of sites on your current multisite installation. By default, you have your primary site listed as the only site in your WordPress multisite network.</p>\n<p>To add a new site, click on the &#8216;Add New&#8217; button at the top.</p>\n<p><img title=\"Add new site\" src=\"https://cdn3.wpbeginner.com/wp-content/uploads/2017/06/addnewsite.png\" alt=\"Add new site\" width=\"550\" height=\"274\" class=\"alignnone size-full wp-image-44899\" /></p>\n<p>On the &#8216;Add New Site&#8217; page, you need to provide the site&#8217;s address. You don&#8217;t need to type the full address, just the part you want to use as subdomain or sub-directory.</p>\n<p>Next, you need to add a site title, and enter the site admin&#8217;s email address.</p>\n<p><img title=\"Adding new site details\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2017/06/newsitepage.png\" alt=\"Adding new site details\" width=\"550\" height=\"350\" class=\"alignnone size-full wp-image-44900\" /></p>\n<p>You can add an admin email address other than the one that you are currently using to manage your multisite network.</p>\n<p>If the email address is not currently in use by another user, then WordPress will create a new user and send the username and password to the email address you enter.</p>\n<p>Once you are done, click on the &#8216;Add Site&#8217; button.</p>\n<p>A new site will be added to your WordPress multisite network. As the network admin, you will receive a new site registration email as well.</p>\n<p>If you created a new user, then that user will receive an email with instructions to set a new password and login.</p>\n<h4 id=\"themesplugins\">Adding Themes and Plugins to Your Multisite Network</h4>\n<p>By default, individual site administrators in a multisite network cannot install themes and plugins on their own.</p>\n<p>As the network admin, you can install the respective plugins and themes, so it&#8217;s available for all sites on your network. </p>\n<p><strong>Installing themes for your multisite network</strong></p>\n<p>To add themes, go to <strong>My Sites &raquo; Network Admin &raquo; Themes</strong> page.</p>\n<p><img title=\"Installed themes on your WordPress multisite network\" src=\"https://cdn4.wpbeginner.com/wp-content/uploads/2017/06/installedthemes.png\" alt=\"Installed themes on your WordPress multisite network\" width=\"550\" height=\"337\" class=\"alignnone size-full wp-image-44901\" /></p>\n<p>On this page, you will see a list of currently installed themes on your WordPress multisite.</p>\n<p>You can make a theme available to other sites by clicking on &#8216;Network Enable&#8217; option under that theme. You can also disable a theme by clicking on &#8216;Network Disable&#8217; link under the theme. Note: Network Disable option will only appear when the theme is enabled.</p>\n<p>To add a new theme, you need to click on the &#8216;Add New&#8217; button at the top of your screen and then <a href=\"https://www.wpbeginner.com/beginners-guide/how-to-install-a-wordpress-theme/\" title=\"Beginners Guide: How to Install a WordPress Theme\">install a WordPress theme</a> as you would normally do.</p>\n<p>Once the new theme is installed, you will be able to make it available to other sites on your network with the &#8216;Network Enable&#8217; option.</p>\n<p>If you need recommendations on which themes to make available to your network, take a look at our picks of the best WordPress themes that you can use. </p>\n<ul>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-free-wordpress-blog-themes/\" title=\"59 Best Free WordPress Blog Themes\">Best free WordPress blog themes</a></li>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-free-wordpress-photography-themes/\" title=\"41 Best Free WordPress Photography Themes (Expert Pick)\">Best free WordPress photography themes</a></li>\n<li><a href=\"https://www.wpbeginner.com/showcase/best-wordpress-multi-purpose-themes/\" title=\"33 Best WordPress Multi-purpose Themes\">Best WordPress multi-purpose themes</a></li>\n</ul>\n<p><strong>Installing plugins for your multisite network</strong></p>\n<p>Similarly, you can visit <strong>My Sites &raquo; Network Admin &raquo; Plugins</strong> page to <a href=\"https://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\" title=\"How to Install a WordPress Plugin – Step by Step for Beginners\">install plugins</a> and click on the &#8216;Network Activate&#8217; link below each plugin to activate them on your multisite network.</p>\n<p><img title=\"Network activate plugins on a WordPress multisite\" src=\"https://cdn2.wpbeginner.com/wp-content/uploads/2017/06/networkactplugins.png\" alt=\"Network activate plugins on a WordPress multisite\" width=\"550\" height=\"321\" class=\"alignnone size-full wp-image-44902\" /></p>\n<p>Following are a few essential WordPress plugins that we recommend for every website: </p>\n<ul>\n<li><a href=\"https://wpforms.com\" title=\"WPForms\">WPForms</a> &#8211; It is the <a href=\"https://www.wpbeginner.com/plugins/5-best-contact-form-plugins-for-wordpress-compared/\" title=\"5 Best Contact Form Plugins for WordPress Compared\">best WordPress contact form plugin</a> and allows you to quickly create beautiful forms using simple drag and drop form builder.</li>\n<li><a href=\"https://wordpress.org/plugins/wordpress-seo\" title=\"Yoast SEO\" rel=\"noopener nofollow\" target=\"_blank\">Yoast SEO</a> &#8211; It is the most comprehensive <a href=\"https://www.wpbeginner.com/showcase/9-best-wordpress-seo-plugins-and-tools-that-you-should-use/\" title=\"9 Best WordPress SEO Plugins and Tools That You Should Use\">WordPress SEO plugin</a> on the market and will help you get more visitors from search engines. </li>\n<li><a href=\"https://www.seedprod.com/\" title=\"SeedProd\">SeedProd</a> &#8211; Site admins may want to see a coming soon page as they work on their sites. SeedProd allows them to add beautiful <a href=\"https://www.wpbeginner.com/plugins/how-to-create-beautiful-coming-soon-pages-in-wordpress-with-seedprod/\" title=\"How to Create Beautiful Coming Soon Pages in WordPress with SeedProd\">coming soon and maintainence mode landing pages</a></li>\n<li><a href=\"https://wordpress.org/plugins/wp-mail-smtp/\" title=\"WP Mail SMTP\" rel=\"noopener nofollow\" target=\"_blank\">WP Mail SMTP</a> &#8211; WP Mail SMTP helps you <a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-fix-wordpress-not-sending-email-issue/\" title=\"How to Fix WordPress Not Sending Email Issue\">fix WordPress not sending email issue</a> by using SMTP server to send crucial multisite registration and notification emails.\n</ul>\n<p>For more plugin recommendations, see our list of the <a href=\"https://www.wpbeginner.com/showcase/24-must-have-wordpress-plugins-for-business-websites/\" title=\"24 Must Have WordPress Plugins for Business Websites in 2018\">essential WordPress plugins</a> for all websites. </p>\n<p><strong>Note:</strong> If you have enabled the Plugins Menu option for site admins in the &#8216;Network Settings&#8217; previously, then the site administrators can activate or deactivate installed plugins on their own. Site admins cannot delete or install a new plugin on their own. </p>\n<h4 id=\"issues\">Troubleshooting WordPress Multisite Issues</h4>\n<p>Most common issues with WordPress multisite network setup occur due to incorrect configuration of wildcard subdomains. Make sure that your web host supports wildcard subdomains.</p>\n<p>Another common issue is that when using WordPress multisite with sub-directories, some users are unable to login to the admin area of their sites after they add the required code in wp-config.php file. </p>\n<p>To fix this, try replacing</p>\n<pre class=\"brush: php; title: ; notranslate\">\r\ndefine(\'SUBDOMAIN_INSTALL\', false);\r\n</pre>\n<p>line in wp-config.php file with</p>\n<pre class=\"brush: php; title: ; notranslate\">\r\ndefine(\'SUBDOMAIN_INSTALL\', \'false\');\r\n</pre>\n<p>Another issue that you may came across is not being able to find users who registered on your network but didn&#8217;t get the activation email. To fix this issue, see our guide on <a href=\"https://www.wpbeginner.com/plugins/how-to-find-pending-unconfirmed-users-in-wordpress/\" title=\"How to Find Pending Unconfirmed Users in WordPress\">how to find pending unconfirmed users in WordPress</a>.</p>\n<p>Later at some point, you or another site owner may want to export a site from multisite to its own WordPress install. This can be easily done. For step by step instructions see our guide on <a href=\"https://www.wpbeginner.com/wp-tutorials/how-to-move-a-site-from-wordpress-multisite-to-single-install/\" title=\"How to Move a Site from WordPress Multisite to Single Install\">how to move a site from WordPress multisite to single install</a>.</p>\n<p>We hope this article helped you learn how to install and setup WordPress multisite network. You may also want to see our step by step <a href=\"https://www.wpbeginner.com/wordpress-security/\" title=\"The Ultimate WordPress Security Guide – Step by Step (2018)\">WordPress security guide</a> to protect and keep your WordPress multisite secure.</p>\n<p>If you liked this article, then please subscribe to our <a href=\"http://youtube.com/wpbeginner?sub_confirmation=1\" title=\"WPBeginner on YouTube\" target=\"_blank\" rel=\"nofollow\">YouTube Channel</a> for WordPress video tutorials. You can also find us on <a href=\"http://twitter.com/wpbeginner\" title=\"WPBeginner on Twitter\" target=\"_blank\" rel=\"nofollow\">Twitter</a> and <a href=\"https://www.facebook.com/wpbeginner\" title=\"WPBeginner on Facebook\" target=\"_blank\" rel=\"nofollow\">Facebook</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://www.wpbeginner.com/wp-tutorials/how-to-install-and-setup-wordpress-multisite-network/\">How to Install and Setup WordPress Multisite Network</a> appeared first on <a rel=\"nofollow\" href=\"https://www.wpbeginner.com\">WPBeginner</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:98:\"https://www.wpbeginner.com/wp-tutorials/how-to-install-and-setup-wordpress-multisite-network/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:3:\"164\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}s:27:\"http://www.w3.org/2005/Atom\";a:1:{s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:4:\"href\";s:32:\"https://www.wpbeginner.com/feed/\";s:3:\"rel\";s:4:\"self\";s:4:\"type\";s:19:\"application/rss+xml\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:44:\"http://purl.org/rss/1.0/modules/syndication/\";a:2:{s:12:\"updatePeriod\";a:1:{i:0;a:5:{s:4:\"data\";s:6:\"hourly\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:15:\"updateFrequency\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"1\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";O:42:\"Requests_Utility_CaseInsensitiveDictionary\":1:{s:7:\"\0*\0data\";a:21:{s:6:\"server\";s:17:\"Sucuri/Cloudproxy\";s:4:\"date\";s:29:\"Tue, 18 Dec 2018 14:24:31 GMT\";s:12:\"content-type\";s:24:\"text/html; charset=UTF-8\";s:14:\"content-length\";s:5:\"20094\";s:11:\"x-sucuri-id\";s:5:\"12016\";s:16:\"x-xss-protection\";s:13:\"1; mode=block\";s:15:\"x-frame-options\";s:10:\"SAMEORIGIN\";s:22:\"x-content-type-options\";s:7:\"nosniff\";s:25:\"strict-transport-security\";a:2:{i:0;s:16:\"max-age=31536000\";i:1;s:16:\"max-age=31536000\";}s:23:\"content-security-policy\";a:2:{i:0;s:26:\"u